<script>
export default {
  name: 'Usako',
  props: {
    mainColor: {
      type: String,
      default: '#EEDDFE'
    },
    cColor: {
      type: String,
      default: '#DEEFDD'
    },
    rColor: {
      type: String,
      default: '#112210'
    }
  },
  data() {
    return {
      marginLeft: 0,
      defaultPositionTop: 200,
      positionTop: 200,
      moveDirection: true,
      jumpDirection: true,
      jumpSet: true,
      jumpUp: null,
      changeDirection: false,
      width: window.innerWidth - 50,
      height: window.innerHeight,
      isIconMove: null,
      isIconFall: null
    }
  },
  computed: {
    skinColor() {
      return { fill: this.mainColor }
    },
    outLineColor() {
      return { fill: this.rColor }
    },
    iconPosition() {
      const turn = this.changeDirection ? 'scale(-1, 1)' : ''
      return {
        'margin-left': this.marginLeft + 'px',
        transform: turn,
        position: 'absolute',
        top: this.positionTop + 'px'
      }
    },
    holePosition() {
      const bgColor = this.isIconMove ? '#00000000' : ''
      return {
        'margin-left': this.marginLeft + 'px',
        background: bgColor
      }
    }
  },
  watch: {
    mainColor(newColor, oldColor) {
      if (newColor !== '') {
        clearInterval(this.isIconFall)
        this.isIconFall = null
        this.positionTop = this.defaultPositionTop
        if (this.isIconMove > 0) {
          this.iconJump()
        } else {
          this.isIconMove = setInterval(this.iconMove, 50)
        }
      }
      if (!newColor) {
        clearInterval(this.isIconMove)
        this.isIconMove = null
        this.isIconFall = setInterval(this.iconFall, 50)
      }
    }
  },
  mounted() {
    if (this.isIconMove) clearInterval(this.isIconMove)
    this.isIconMove = setInterval(this.iconMove, 50)
    setInterval(window.addEventListener('resize', this.handleResize), 50)
    if (this.isHoleDrop) {
      clearInterval(this.isIconMove)
    }
  },
  beforeDestroy() {
    setInterval(window.removeEventListener('resize', this.handleResize), 50)
  },
  methods: {
    iconMove() {
      const maxLeft = this.width - 100
      if (this.moveDirection && this.marginLeft < maxLeft) {
        this.marginLeft += 2
        this.changeDirection = false
      } else if (!this.moveDirection && this.marginLeft > 0) {
        this.marginLeft -= 2
        this.changeDirection = true
      }
      if (this.marginLeft >= maxLeft) this.moveDirection = false
      if (this.marginLeft <= 0) this.moveDirection = true
    },
    iconFall() {
      this.positionTop += 20
      const falling = this.height - 90
      if (this.positionTop > falling) {
        clearInterval(this.isIconFall)
        this.isIconFall = null
      }
    },
    iconJump() {
      this.jumpSet = true
      if (this.jumpUp) clearInterval(this.jumpUp)
      this.jumpUp = setInterval(this.iconJumpAction, 10)
    },
    iconJumpAction(id) {
      if (!this.jumpSet) {
        clearInterval(this.jumpUp)
        return
      }
      const maxUp = 20
      if (this.jumpDirection && this.positionTop >= maxUp) {
        this.positionTop -= 5
      } else if (
        !this.jumpDirection &&
        this.positionTop <= this.defaultPositionTop
      ) {
        this.positionTop += 5
      }
      if (this.positionTop <= maxUp) this.jumpDirection = false
      if (this.positionTop >= this.defaultPositionTop) {
        this.jumpDirection = true
        this.jumpSet = false
        clearInterval(this.jumpUp)
      }
    },
    handleResize() {
      this.width = window.innerWidth - 50
      this.height = window.innerHeight
    }
  }
}
</script>

